var targets = []int{1, 3, 5, 6, 4, 5, 3, 5, 8, 0, 1, 4, 6, 11, 181, 22, 313, 666}

func main() {

	for x, tvals := range testvals {
		log.Printf("For input 3Sum [%+v]\n", threeSumClosest(tvals, targets[x]))
		log.Print("-----\n")
	}
}

func threeSumClosest(nums []int, target int) int {

	if len(nums) < 1 {
		return 0
	}
	if len(nums) < 2 {
		return nums[0]
	}
	if len(nums) < 3 {
		return nums[0] + nums[1]
	}

	closestSum := 999999
	closestDiff := 999999
	// then create a permutation, with sort and compare
	for fi := 0; fi < len(nums)-2; fi++ {
		fv := nums[fi]
		for se := fi + 1; se < len(nums)-1; se++ {
			sv := nums[se]

			for th := se + 1; th < len(nums); th++ {
				tv := nums[th]

				sum := fv + sv + tv
				diff := abso(sum - target)
				if diff < closestDiff {
					closestDiff = diff
					closestSum = sum
				}

			}
		}
	}

	return closestSum
}

func abso(x int) int {

	if x < 0 {
		return -x
	}
	return x
}
