var targets = []int{4, 1, 3, 5, 6, 4, 5, 3, 0, 0, 0, 1, 4, 6, 11, 181, 22, 313, 666}

func main() {

	for x, tvals := range testvals {
		log.Printf("For target[%d], 4Sum [%+v]\n", targets[x], fourSum(tvals, targets[x]))
		log.Print("-----\n")
	}
}

func fourSum(nums []int, target int) [][]int {

	var reta [][]int

	if len(nums) < 4 {
		return reta
	}

	resMap := make(map[string]bool)
	// then create a permutation, with sort and compare
	for fi := 0; fi < len(nums)-3; fi++ {
		fv := nums[fi]
		for se := fi + 1; se < len(nums)-2; se++ {
			sv := nums[se]
			for th := se + 1; th < len(nums)-1; th++ {
				tv := nums[th]
				for fo := th + 1; fo < len(nums); fo++ {
					fov := nums[fo]

					sum := fv + sv + tv + fov
					if sum == target {
						toAdd := []int{fv, sv, tv, fov}
						toAdd = sort4sum(toAdd)
						toAddStr := fmt.Sprintf("%d,%d,%d,%d", toAdd[0], toAdd[1], toAdd[2], toAdd[3])
						if _, k := resMap[toAddStr]; !k {
							resMap[toAddStr] = true
							reta = append(reta, toAdd)
							log.Printf(" >>> resMap apres append: %+v", resMap)
						}
					}

				}
			}
		}
	}

	return reta
}

func sort4sum(e []int) []int {

	//log.Printf(" >>> input: %+v", e)

	for {
		changed := false
		for i := 0; i < 3; i++ {
			if e[i] < e[i+1] {
				e[i], e[i+1] = e[i+1], e[i]
				changed = true
			}
		}
		if changed == false {
			break
		}
	}
	//log.Printf(" <<< output: %+v", e)
	return e
}
